% Chapter 5: The Performance of Feedback Control Systems% CP5.3%w1=2; z1=0;w2=2; z2=0.1;w3=1; z3=0;w4=1; z4=0.2;t=0:0.1:20;%num1=[w1^2]; den1=[1 2*z1*w1 w1^2]; sys1=tf(num1,den1);[y1,T1]=impulse(sys1,t);%num2=[w2^2]; den2=[1 2*z2*w2 w2^2]; sys2=tf(num2,den2);[y2,T2]=impulse(sys2,t);%num3=[w3^2]; den3=[1 2*z3*w3 w3^2]; sys3=tf(num3,den3);[y3,T3]=impulse(sys3,t);%num4=[w4^2]; den4=[1 2*z4*w4 w4^2]; sys4=tf(num4,den4);[y4,T4]=impulse(sys4,t);%subplot(221),plot(T1,y1),title('\omega_n=2, \zeta=0')subplot(222),plot(T2,y2),title('\omega_n=2, \zeta=0.1')subplot(223),plot(T3,y3),title('\omega_n=1, \zeta=0')subplot(224),plot(T4,y4),title('\omega_n=1, \zeta=0.2')