% Chapter 7: The Root Locus Method% CP7.1%%% Part (a)%num=25; den=[1 10 40 25]; sys=tf(num,den);figure(1)rlocus(sys);title('(a)');%% Part (b)%figure(2)num=[1 10]; den=[1 2 10]; sys=tf(num,den);rlocus(sys);title('(b)');%% Part (c)%figure(3)num=[1 2 4]; den=[1 5 10 0]; sys=tf(num,den);rlocus(sys);title('(c)');%% Part (d)%figure(4)num=[1 6 6 12 6 4]; den=[1 4 5 1 1 12 1]; sys=tf(num,den);rlocus(sys);title('(d)');